import axios from 'axios'
import { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'
import './view.css'
export const View = () => {
    const [user, setUser] = useState({})
    const {id}=useParams()
    const getSingleUser = async(id)=>{
        const res= await axios.get(`http://localhost:5000/users/${id}`)
        if(res.status === 200){
            setUser({...res.data})
        }
    }

    useEffect(() => {
      if(id){
        getSingleUser(id)
      }
    

    }, [id])
    
  return (
    <div className='view'>
    <div className="viev-item">
        <b>ID: </b> <span>123123</span><br/>
        <b>Name: </b> <span>123123</span><br/>
        <b>Email: </b> <span>123123</span><br/>
        <b>Country: </b> <span>123123</span><br/>
        <b>Contact: </b> <span>123123</span><br/>
    </div>
    <div>
       <Link to={`/update/${user.id}`}><button>Edit</button></Link> 
       <Link to="/"><button>Back</button></Link> 
    </div>

    </div>
  )
}
